import {useQuery, gql} from '@apollo/client'


export default function Wallpaper(){

    const WALLPAPER = gql`
        Media(sort: POPULARITY_DESC, isAdult: false, type: ANIME) {
            bannerImage
        }
        }
    }
    `;

    const {error, loading, data} = useQuery(WALLPAPER);

    useEffect(()=>{
       }, [data]
    );

    return(
        <Fragment>
        {data.Media.map((img) => (
            <img src={img.bannerImage}/>
        ))}
        </Fragment>

    );
}
