import {gql} from '@apollo/client'
import React from "react";

export const GENRE_QUERY = gql`
    query GENRE_QUERY($genre: String){ 
        Page(page: 1, perPage: 50){
          pageInfo{
            lastPage
          }
          media(type: ANIME, isAdult: false, genre: $genre){
            title{
              english
              romaji
            }
            genres
            description
            coverImage{
              medium
              large
              extraLarge
            }
            studios{
              nodes{
                name
              }
            }
          }
        }
    }
`
