import React, { Fragment, useEffect, useState  } from "react";
import ShowCard from './ShowCard.js';
import Header from "../global_elements/Header"
import TopSpace from "../global_elements/TopSpacer"
import {AIRING_NOW} from '../../GraphQL/Queries'

import {useQuery} from '@apollo/client'
import './Homepage.css';

import { Container, Row, Col, Button } from "react-bootstrap";

//will be used as next page to list
function Homepage(){

    const {error, loading, data} = useQuery(AIRING_NOW);
    useEffect(()=>{
      console.log(data)
    }, [data]
  );
      if (loading) return <p>Loading...</p>
      if (error) return <p>Error :(</p>

    // this is to check and make sure names are displayed since some of them dont have english names
    let name_array = [data.Page.media.length];
    for (let i = 0; i < data.Page.media.length; i++) {
        let temp_name = data.Page.media[i].title.english;
        if (temp_name == null){
            temp_name = data.Page.media[i].title.romaji;
        }
        name_array[i] = temp_name;       
    }

    let state1 = {
        name: name_array[0],
        image: data.Page.media[0].coverImage.large,
        genre: data.Page.media[0].genres.join(', '),
        studio: data.Page.media[0].studios.nodes[0].name
    }


    let state2 = {
        name: name_array[1],
        image: data.Page.media[1].coverImage.large,
        genre: data.Page.media[1].genres.join(', '),
        studio: data.Page.media[1].studios.nodes[0].name
    }

    let state3 = {
        name:  name_array[2],
        image: data.Page.media[2].coverImage.large,
        genre: data.Page.media[2].genres.join(', '),
        //studio: data.Page.media[2].studios.nodes.name
    }

    let state4 = {
        name:  name_array[3],
        image: data.Page.media[3].coverImage.large,
        genre: data.Page.media[3].genres.join(', '),
        //studio: data.Page.media[3].studios.nodes.name
    }
    //state1.name = data.Media[0].title.english

    return(
        <Fragment>
            <Header />
            <Container>
                <TopSpace />
                <Row id="feed_body">
                    <Col id="feed_col">
                        <Row id="subtitle">
                            <Col id="pink">
                                <strong id="recommended">Recommended</strong>
                            </Col>
                            <Col id="grey"><span></span></Col>
                        </Row>
                        <Row id="main_content">


                        </Row>
                    </Col>
                    <Col id="spacing"></Col>
                    <Col id="feed_col">
                        <Row id="subtitle">
                            <Col id="purple">
                                <strong id="recommended">Airing</strong>
                            </Col>
                            <Col id="grey"><span></span></Col>
                        </Row>
                        <Row id="main_content">
                            <ShowCard 
                                    name={state1.name}
                                    image={state1.image}
                                    genre={state1.genre}
                                    studio={state1.studio}/>
                                <ShowCard 
                                    name={state2.name}
                                    image={state2.image}
                                    genre={state2.genre}
                                    studio={state2.studio}/>
                                <ShowCard 
                                    name={state3.name}
                                    image={state3.image}
                                    genre={state3.genre}
                                    studio={state3.studio}/>
                                <ShowCard 
                                    name={state4.name}
                                    image={state4.image}
                                    genre={state4.genre}
                                    studio={state4.studio}/>

                        </Row>
                    </Col>
                    <Col id="spacing"></Col>
                    <Col id="feed_col">
                        <Row id="subtitle">
                            <Col id="pink">    
                                <span><strong>Try This</strong></span>
                            </Col>
                            <Col id="grey"><span></span></Col>
                        </Row>
                        <Row id="main_content">
                            <ShowCard 
                                name="Test Anime" 
                                image="[image]" 
                                genre= "action, adventure, tragedy, drama" 
                                studio="New Studio"/>
                            <ShowCard 
                                name="Test Anime 2" 
                                image="[image]" 
                                genre= "romance, slice of life, comedy, shoujo" 
                                studio="Veteran Studio"/>
                        </Row>
                    </Col>
                    <Col id="spacing"></Col>
                    <Col id="feed_col">
                        <Row id="subtitle">
                            <Col id="purple">    
                                <span><strong>Trending</strong></span>
                            </Col>
                            <Col id="grey"><span></span></Col>
                        </Row>
                        <Row id="main_content">
                            <ShowCard 
                                name="Test Anime" 
                                image="[image]" 
                                genre= "action, adventure, tragedy, drama" 
                                studio="New Studio"/>
                            <ShowCard 
                                name="Test Anime 2" 
                                image="[image]" 
                                genre= "romance, slice of life, comedy, shoujo" 
                                studio="Veteran Studio"/>
                        </Row>
                    </Col>
                </Row>
            </Container>
        </Fragment>

    );
}

export default Homepage;
