import React, { Fragment, useEffect, useState } from "react";
import "./HomePageShowCard.css";
//frame for the anime to show
import { Container, Row, Col } from "react-bootstrap";
import { Link } from "react-router-dom";
//import GenreQuery from "./dynamic_genre_query";
import gql from 'graphql-tag'


function HomePageShowCard(props){
    let genreCount = props.genre.length
    const genreList = []
    
    return(
        <Fragment>
                <Container id="HomePage_cards">
                    <Row>
                        <Col>
                            <img id="HomePage_card_image" src={props.image} class="center"/>
                        </Col>
                        <Col id="HomePage_card_description">
                            <Row id="HomePage_card_title">
                                <p id="HomePage_card_text">{props.name}</p>
                            </Row>
                            <Row id="HomePage_card_genres">
                                <p id="HomePage_card_text">
                                    {props.genre.map((genres) =>
                                        {
                                        if (genreCount < 2)
                                            return <Link id="tag_links" to={{
                                                        pathname:`/ShowMore/${genres}`,
                                                        state: {
                                                            query: gql`query($genre: String) {
                                                                Page(page: 1, perPage: 50){
                                                                    media(type: ANIME, isAdult: false, genre: $genre){
                                                                      title{
                                                                        english
                                                                        romaji
                                                                      }
                                                                      genres
                                                                      description
                                                                      coverImage{
                                                                        medium
                                                                        large
                                                                        extraLarge
                                                                      }
                                                                      studios{
                                                                        nodes{
                                                                          name
                                                                        }
                                                                      }
                                                                    }
                                                                  }
                                                            }`,
                                                            variables: {
                                                                genre: genres
                                                            }
                                                        }
                                                    }}>{genres}</Link>
                                        else 
                                            genreCount--;
                                            return  <Fragment>
                                                        <Link id="tag_links" to={{
                                                            pathname:`/ShowMore/${genres}`,
                                                            state: {
                                                                query: gql`query($genre: String) {
                                                                    Page(page: 1, perPage: 50){
                                                                        media(type: ANIME, isAdult: false, genre: $genre){
                                                                          title{
                                                                            english
                                                                            romaji
                                                                          }
                                                                          genres
                                                                          description
                                                                          coverImage{
                                                                            medium
                                                                            large
                                                                            extraLarge
                                                                          }
                                                                          studios{
                                                                            nodes{
                                                                              name
                                                                            }
                                                                          }
                                                                        }
                                                                      }
                                                                }`,
                                                                variables: {
                                                                    genre: genres
                                                                }
                                                            }
                                                            }}>{genres}
                                                        </Link>
                                                        <a>, </a>
                                                    </Fragment>
                                        }
                                    )}
                                </p>
                            </Row>
                            <Row id="HomePage_card_studio">
                                <p id="HomePage_card_text">{props.studio}</p>
                            </Row>
                        </Col>
                    </Row>
                </Container>
        </Fragment>
    );
}

export default HomePageShowCard;
