import {gql} from '@apollo/client'
import React from "react";

export default function GenreQuery(props){
  const genreQuery = (genres) =>{
    gql`
    query(${genre}: String!)
    {
      Page(page: 1, perPage: 50){
        media(type: ANIME, isAdult: false, genre: ${genres}){
          title{
            english
            romaji
          }
          genres
          description
          coverImage{
            medium
            large
            extraLarge
          }
          studios{
            nodes{
              name
            }
          }
        }
      }
    }
    `
  }

  console.log(props);
  return genreQuery(props);
}
