import {gql, client} from '@apollo/client'

export default function GenreQuery(props){
  const genreQuery = gql`
  {
    Page(page: 1, perPage: 50){
      media(type: ANIME, isAdult: false, genre: $genres){
        title{
          english
          romaji
        }
        genres
        description
        coverImage{
          medium
          large
          extraLarge
        }
        studios{
          nodes{
            name
          }
        }
      }
    }
  }
  `

  client.query({
    query: genreQuery,
    variables:{
      genres: props
    }
  })

  return genreQuery(props);
}
