import {gql} from '@apollo/client'

export const LOAD_ANIME = gql`
query{
    Page(page: 1, perPage: 4) {
      pageInfo {
        lastPage
      }
      media(type: ANIME, isAdult: false, season: SPRING, seasonYear: 2021) {
        title {
          english
          romaji
        }
        genres
        description
        coverImage {
          medium
          large
          extraLarge
        }
      }
    }
`


/*Media(type: ANIME, search: "March Comes in like a Lion Season 2"){
