import {gql} from '@apollo/client'
import React from "react";

export default function GenreQuery(props){
    const GENRE_QUERY = gql
    `
    {
        Page(page: 1, perPage: 50){
          pageInfo{
            lastPage
          }
          media(type: ANIME, isAdult: false, genre: ${props}){
            title{
              english
              romaji
            }
            genres
            description
            coverImage{
              medium
              large
              extraLarge
            }
            studios{
              nodes{
                name
              }
            }
          }
        }
    }`

    return GENRE_QUERY
}
