import {gql} from '@apollo/client'
import React from "react";

export default function GenreQuery(props){
    const GENRE_QUERY = gql
    `
    {
        Page(page: 1, perPage: 50){
          pageInfo{
            lastPage
          }
          media(type: ANIME, isAdult: false, genre: $genres){
            title{
              english
              romaji
            }
            genres
            description
            coverImage{
              medium
              large
              extraLarge
            }
            studios{
              nodes{
                name
              }
            }
          }
        }
    }`

    const genreQueryStr = GENRE_QUERY.loc.source.body
    genreQueryStr.split('\n')
    genreQueryStr.slice(1, 26)
    genreQueryStr.replace(`$genres`, props)

    const finalQuery = gql`
        ${genreQueryStr.join(`\n`)}
    `
    return finalQuery
}
