import React, { Fragment, useEffect } from "react";
import "./GeneralShowMore.css";
import TopSpace from "../../components/global_elements/TopSpacer";
import ListingPageShowCard from '../../components/ListingPageShowCard';
import { Container } from "react-bootstrap";
import { Col, Row, Grid } from 'react-flexbox-grid'
import { chunk } from 'lodash'
import { Link, useParams, useLocation } from 'react-router-dom';
import {useQuery, gql} from '@apollo/client'

export default function GeneralShowMore(){
    const QUERY = gql`
    query AiringTrendingShowMore($page: Int, $season: String, $seasonYear: Int, $sort: String){
      Page(page: $page, perPage: 48) {
        media(season: $season, seasonYear: $year, sort: $sort, isAdult: false, type: ANIME) {
          id
          title {
            romaji
            english
          }
          genres
          description(asHtml: false)
          coverImage {
            medium
            large
            extraLarge
          }
          studios {
            nodes {
              name
            }
          }
        }
      }
    }
  `;

    const params = useParams();
    let variables = {
        page: 1,
        season: "",
        seasonYear: "",
        sort: ""
    };
    if (params.queryName == "airing_now")
    {
        variables.page = params.pageNum,
        variables.season = "SPRING",
        variables.seasonYear = 2021
    }
    else if (params.queryName == "trending")
    {
        variables.page = params.pageNum,
        variables.sort = "TRENDING_DESC"
    }
    const {error, loading, data, refetch} = useQuery(QUERY, variables);

    useEffect(()=>{
        refetch();
    }, []
    );



    if (loading) return <p>Loading1...</p>
    else if (error) return <p>Error1 :(</p>
    else{
        const rows = chunk(data.Page.media, 4)
        return (
            <Fragment>
                <TopSpace />
                <Container className="">
                    <Row id="back_btn_space">
                        <Link className="back_btn" to={"/Anime"}><strong>Back</strong></Link>
                    </Row>
                    <Grid>
                        {rows.map((cols) => (
                            <Row>
                                {cols.map((col) => (
                                    <Col md={3} id="grid-content">
                                        <ListingPageShowCard   
                                            image={col.coverImage.large} 
                                            name={(col.title.english==null)
                                                ? col.title.romaji :
                                                col.title.english}
                                            genre={col.genres}
                                            studio={(col.studios.nodes.length > 5) ? col.studios.nodes.slice(0,6).map(studio_name => 
                                                studio_name.name).join(', ') :
                                                col.studios.nodes.map(studio_name => 
                                                studio_name.name).join(', ')}/>
                                    </Col>
                                )
                                )}
                            </Row>
                        )
                        )}
                        {(params.pageNum > 1) ?
                                ((params.pageNum > data.length - Number(1)) ?
                                    <Link to={{pathname:`/ShowMore/${params.queryName}/${Number(params.pageNum) - 1}`}}>
                                        Previous Page
                                    </Link> :
                                    <Fragment>
                                            <Link to={{pathname:`/ShowMore/${params.queryName}/${Number(params.pageNum) - 1}`}}>
                                                Previous Page
                                            </Link>
                                            <Link to={{pathname:`/ShowMore/${params.queryName}/${Number(params.pageNum) + 1}`}}>
                                                Next Page
                                            </Link> 
                                    </Fragment> ): 
                                <Link to={{pathname:`/ShowMore/${params.queryName}/${Number(params.pageNum) + 1}`}}>
                                    Next Page
                                </Link>
                            }
                    </Grid>
                </Container>
            </Fragment>
        );
    }
}
